
public class areaTrapezioExemplo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double b, B, h, area;
		
		b = 6.0;
		B = 8.0;
		h = 5.0;
		
		area = (b + B) / 2.0 * h;
		
		System.out.println(area);
		
		//Exemplo de casting:
		
		int x, y;
		double resultado;
		
		x = 5;
		y = 2;
		
		resultado = (double) x/y;
		
		System.out.println(resultado);
		
		
	}

}
